CREATE TABLE IF NOT EXISTS bill (id INT NOT NULL AUTO_INCREMENT, name VARCHAR(35), agreement VARCHAR(35), end_date VARCHAR(35), amount NUMERIC(8,2), description VARCHAR(35), validation_data_base VARCHAR(1), enabled_to_pay BOOLEAN, status VARCHAR(1), ref_value VARCHAR(35) UNIQUE, ref_name VARCHAR(35), acct_id VARCHAR(35), acct_type_code VARCHAR(5), acct_type_code_desc VARCHAR(35), identification VARCHAR(10), user_name VARCHAR(35), PRIMARY KEY (id));