CREATE TABLE IF NOT EXISTS bill (id INT NOT NULL AUTO_INCREMENT, name VARCHAR(35), agreement VARCHAR(35), endDate VARCHAR(35), amount NUMERIC(8,2), description VARCHAR(35), validationDataBase VARCHAR(1), enabledToPay BOOLEAN, status VARCHAR(1), refvalue VARCHAR(35), refname VARCHAR(35), acctId VARCHAR(35), acctTypeCode VARCHAR(1), acctTypeCodeDesc VARCHAR(35), identification VARCHAR(10), userName VARCHAR(35), PRIMARY KEY (id));